// src/pages/AdminDashboardPage.js
import React from 'react';
import './DashboardPage.css'; // We'll reuse the main dashboard styles
import './AdminDashboard.css'; // And add specific admin styles

const mockUsers = [
    { id: 1, name: 'Rohan Sharma', email: 'rohan.s@email.com', role: 'Screening Member' },
    { id: 2, name: 'Priya Singh', email: 'priya.s@email.com', role: 'Screening Member' },
    { id: 3, name: 'Amit Kumar', email: 'amit.k@email.com', role: 'Applicant' },
    { id: 4, name: 'Archit Yadav', email: 'archit.y@email.com', role: 'Admin' },
];

const AdminDashboardPage = () => {
    const handleCreateRole = () => {
        alert('In a real app, this would open a form to create a new role with specific permissions.');
    };

    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                <h1>Admin Dashboard</h1>
                <span>DRDO Portal</span>
            </header>
            <main className="dashboard-main">
                <div className="admin-card">
                    <h3>User Management</h3>
                    <p>View and manage user roles within the system.</p>
                    <table className="user-table">
                        <thead>
                        <tr>
                            <th>Name</th>
                            <th>Email</th>
                            <th>Current Role</th>
                            <th>Action</th>
                        </tr>
                        </thead>
                        <tbody>
                        {mockUsers.map(user => (
                            <tr key={user.id}>
                                <td>{user.name}</td>
                                <td>{user.email}</td>
                                <td><span className={`role-pill role-${user.role.split(' ')[0].toLowerCase()}`}>{user.role}</span></td>
                                <td><button className="action-button">Assign Role</button></td>
                            </tr>
                        ))}
                        </tbody>
                    </table>
                </div>
                <div className="admin-card">
                    <h3>Role Management</h3>
                    <p>Define new roles and their permissions.</p>
                    <button className="create-role-button" onClick={handleCreateRole}>+ Add New Role</button>
                </div>
            </main>
        </div>
    );
};

export default AdminDashboardPage;
