// src/components/dashboard/FileUploader.js
import React, { useState } from 'react';
import './Dashboard.css';

const FileUploader = () => {
    const [fileName, setFileName] = useState('');

    const handleFileChange = (e) => {
        if (e.target.files && e.target.files.length > 0) {
            setFileName(e.target.files[0].name);
        }
    };

    const handleUpload = () => {
        if (!fileName) {
            alert('Please select a file first.');
            return;
        }
        // Simulate upload
        alert(`Simulating upload for ${fileName} and processing documents...`);
        // In a real app, this would trigger a backend process.
    };

    return (
        <div className="card">
            <h3>Upload Applicant CSV</h3>
            <p>Upload a CSV with applicant details. The system will then process associated documents.</p>
            <div className="uploader-area">
                <input type="file" id="csv-upload" accept=".csv" onChange={handleFileChange} style={{ display: 'none' }} />
                <label htmlFor="csv-upload" className="upload-label">
                    Choose File
                </label>
                <span className="file-name">{fileName || 'No file selected'}</span>
            </div>
            <button onClick={handleUpload} className="upload-button">Upload and Process</button>
        </div>
    );
};

export default FileUploader;
