// src/pages/AdminDashboardPage.js
import React, { useState, useMemo } from 'react';
import './DashboardPage.css';
import './AdminDashboard.css';
import Modal from '../components/common/Modal';
import { initialUsers, initialRoles } from '../data/mockData';
import { useAuth } from '../context/AuthContext'; // Import the useAuth hook

const AdminDashboardPage = () => {
    const [users, setUsers] = useState(initialUsers);
    const [roles, setRoles] = useState(initialRoles);
    const [isAssignModalOpen, setAssignModalOpen] = useState(false);
    const [isAddRoleModalOpen, setAddRoleModalOpen] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);
    const [newRoleName, setNewRoleName] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [roleFilter, setRoleFilter] = useState('All');
    const { logout } = useAuth(); // Get the logout function from our context

    const filteredUsers = useMemo(() => {
        return users.filter(user => {
            const matchesRole = roleFilter === 'All' || user.role === roleFilter;
            const matchesSearch = user.email.toLowerCase().includes(searchQuery.toLowerCase());
            return matchesRole && matchesSearch;
        });
    }, [users, searchQuery, roleFilter]);

    const handleOpenAssignModal = (user) => { setSelectedUser(user); setAssignModalOpen(true); };
    const handleAssignRole = (newRole) => {
        setUsers(users.map(user => user.id === selectedUser.id ? { ...user, role: newRole } : user));
        setAssignModalOpen(false);
    };
    const handleOpenAddRoleModal = () => { setNewRoleName(''); setAddRoleModalOpen(true); };
    const handleAddNewRole = () => {
        if (newRoleName && !roles.includes(newRoleName)) {
            setRoles([...roles, newRoleName]);
            setAddRoleModalOpen(false);
        } else {
            alert('Please enter a valid, unique role name.');
        }
    };
    const getRoleClass = (role) => {
        const roleKey = role.split(' ')[0].toLowerCase();
        const validRoles = ['admin', 'screening', 'applicant'];
        return validRoles.includes(roleKey) ? `role-${roleKey}` : 'role-default';
    }

    return (
        <>
            <div className="dashboard-container">
                <header className="dashboard-header">
                    <h1>Admin Dashboard</h1>
                    {/* --- LOGOUT BUTTON ADDED HERE --- */}
                    <button className="logout-button" onClick={logout}>Logout</button>
                </header>
                <main className="dashboard-main">
                    <div className="admin-card">
                        <h3>User Management</h3>
                        <p>View and manage user roles within the system.</p>
                        <div className="controls-container">
                            <input type="text" placeholder="Search by user email..." className="search-input" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} />
                            <select className="filter-select custom-select" value={roleFilter} onChange={(e) => setRoleFilter(e.target.value)}>
                                <option value="All">All Roles</option>
                                {roles.map(role => (<option key={role} value={role}>{role}</option>))}
                            </select>
                        </div>
                        <table className="user-table">
                            <thead>
                            <tr><th>Name</th><th>Email</th><th>Current Role</th><th>Action</th></tr>
                            </thead>
                            <tbody>
                            {filteredUsers.length > 0 ? (filteredUsers.map(user => (
                                <tr key={user.id}>
                                    <td>{user.name}</td>
                                    <td>{user.email}</td>
                                    <td><span className={`role-pill ${getRoleClass(user.role)}`}>{user.role}</span></td>
                                    <td><button className="action-button" onClick={() => handleOpenAssignModal(user)}>Assign Role</button></td>
                                </tr>
                            ))) : (
                                <tr><td colSpan="4" style={{ textAlign: 'center', padding: '2rem', color: '#6a737d' }}>No users found matching your criteria.</td></tr>
                            )}
                            </tbody>
                        </table>
                    </div>
                    <div className="admin-card">
                        <h3>Role Management</h3>
                        <p>Define new roles and their permissions.</p>
                        <button className="create-role-button" onClick={handleOpenAddRoleModal}>+ Add New Role</button>
                    </div>
                </main>
            </div>
            <Modal title="Assign Role" isOpen={isAssignModalOpen} onClose={() => setAssignModalOpen(false)}>
                {selectedUser && (<div>
                    <p>Assign a new role to <strong>{selectedUser.name}</strong>.</p>
                    <select className="custom-select" style={{ width: '100%', border: '1px solid #dcdfe6', borderRadius: '6px' }} defaultValue={selectedUser.role} onChange={(e) => handleAssignRole(e.target.value)}>
                        {roles.map(role => <option key={role} value={role}>{role}</option>)}
                    </select>
                </div>)}
            </Modal>
            <Modal title="Add New Role" isOpen={isAddRoleModalOpen} onClose={() => setAddRoleModalOpen(false)}>
                <div className="form-group"><label htmlFor="newRole">Role Name</label>
                    <input type="text" id="newRole" value={newRoleName} onChange={(e) => setNewRoleName(e.target.value)} placeholder="e.g., Document Verifier" style={{ width: '100%', padding: '0.8rem', border: '1px solid #dcdfe6', borderRadius: '6px' }} />
                </div>
                <button className="form-button" onClick={handleAddNewRole}>Create Role</button>
            </Modal>
        </>
    );
};
export default AdminDashboardPage;
