// src/context/AuthContext.js
import React, { createContext, useState, useContext, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';

const AuthContext = createContext(null);
const API_URL = 'http://localhost:8000/api/'; // Our Django API base URL

export const AuthProvider = ({ children }) => {
    // We will store the tokens in state, and user details in another
    const [authTokens, setAuthTokens] = useState(() => localStorage.getItem('drdo-authTokens') ? JSON.parse(localStorage.getItem('drdo-authTokens')) : null);
    const [user, setUser] = useState(() => localStorage.getItem('drdo-authTokens') ? JSON.parse(atob(localStorage.getItem('drdo-authTokens').split('.')[1])) : null);
    const [isLoading, setIsLoading] = useState(true);
    const navigate = useNavigate();

    // The new login function that talks to Django
    const login = async (email, password) => {
        try {
            // Make a POST request to the token endpoint
            const response = await axios.post(API_URL + 'token/', {
                username: email, // Django's default User model uses 'username'
                password: password,
            });

            if (response.status === 200) {
                const data = response.data;
                setAuthTokens(data);
                // Decode the user info from the token payload
                setUser(JSON.parse(atob(data.access.split('.')[1])));

                // Store tokens in localStorage
                localStorage.setItem('drdo-authTokens', JSON.stringify(data));

                // Simple role-based navigation
                if (email.includes('admin')) {
                    navigate('/admin-dashboard');
                } else {
                    navigate('/dashboard'); // Default for others
                }
            }
        } catch (error) {
            console.error("Login Error:", error);
            alert("Something went wrong! Check credentials.");
        }
    };

    const logout = () => {
        setAuthTokens(null);
        setUser(null);
        localStorage.removeItem('drdo-authTokens');
        navigate('/login');
    };

    // This effect makes sure we are still logged in on page refresh
    useEffect(() => {
        setIsLoading(false);
    }, [authTokens]);

    const value = {
        isAuthenticated: !!user,
        user,
        isLoading,
        login,
        logout,
    };

    return (
        <AuthContext.Provider value={value}>
            {isLoading ? null : children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => {
    return useContext(AuthContext);
};
