// src/pages/ApplicantDashboardPage.js
import React from 'react';
import './DashboardPage.css'; // Reuse main dashboard styles

const ApplicantDashboardPage = () => {
    return (
        <div className="dashboard-container">
            <header className="dashboard-header">
                <h1>Applicant Dashboard</h1>
                <span>DRDO Portal</span>
            </header>
            <main className="dashboard-main">
                <div style={{ background: 'white', padding: '2rem', borderRadius: '12px', textAlign: 'center', boxShadow: '0 4px 12px rgba(0,0,0,0.05)' }}>
                    <h2>Welcome, Applicant!</h2>
                    <p style={{ color: '#6a737d', marginTop: '1rem' }}>
                        Your application status and any required actions will appear here.
                    </p>
                    <p style={{ color: '#6a737d' }}>
                        Currently, there are no pending actions.
                    </p>
                </div>
            </main>
        </div>
    );
};

export default ApplicantDashboardPage;
