// src/pages/AdminDashboardPage.js
import React, { useState, useMemo, useEffect } from 'react';
import axios from 'axios';
import './DashboardPage.css';
import './AdminDashboard.css';
import Modal from '../components/common/Modal';
import { useAuth } from '../context/AuthContext';
import { initialRoles } from '../data/mockData';

const AdminDashboardPage = () => {
    const { logout, user } = useAuth();
    const [users, setUsers] = useState([]);
    const [roles, setRoles] = useState(initialRoles);

    // --- UPDATED useEffect to send the Auth Token ---
    useEffect(() => {
        // Get the tokens from where our AuthContext stored them
        const tokens = JSON.parse(localStorage.getItem('drdo-authTokens'));
        if (!tokens) return; // Don't proceed if no tokens

        // Create the configuration for the authenticated request
        const config = {
            headers: {
                // This is the crucial part: "Bearer" + the access token
                'Authorization': `Bearer ${tokens.access}`
            }
        };

        // Make the authenticated GET request
        axios.get('http://localhost:8000/api/users/', config)
            .then(response => {
                // We need to map the Django field names to our frontend field names
                const formattedUsers = response.data.map(u => ({
                    id: u.id,
                    name: u.username, // Use 'username' for the 'name' field
                    email: u.email,
                    role: u.is_staff ? 'Admin' : 'Applicant' // Simple role mapping
                }));
                setUsers(formattedUsers);
            })
            .catch(error => {
                console.error("There was an error fetching the protected user data!", error);
                // This could happen if the token has expired.
            });
    }, []); // Empty array means this runs once on mount

    // All the other functions and JSX remain the same...
    const filteredUsers = useMemo(() => {
        return users.filter(u => (roleFilter === 'All' || u.role === roleFilter) && u.email.toLowerCase().includes(searchQuery.toLowerCase()));
    }, [users, searchQuery, roleFilter]);
    const [isAssignModalOpen, setAssignModalOpen] = useState(false);
    const [isAddRoleModalOpen, setAddRoleModalOpen] = useState(false);
    const [selectedUser, setSelectedUser] = useState(null);
    const [newRoleName, setNewRoleName] = useState('');
    const [searchQuery, setSearchQuery] = useState('');
    const [roleFilter, setRoleFilter] = useState('All');
    const handleAssignRole = (newRole) => { setUsers(users.map(u => u.id === selectedUser.id ? { ...u, role: newRole } : u)); setAssignModalOpen(false); };
    const handleOpenAssignModal = (user) => { setSelectedUser(user); setAssignModalOpen(true); };
    const handleOpenAddRoleModal = () => { setNewRoleName(''); setAddRoleModalOpen(true); };
    const handleAddNewRole = () => { if (newRoleName && !roles.includes(newRoleName)) { setRoles([...roles, newRoleName]); setAddRoleModalOpen(false); } else { alert('Please enter a valid, unique role name.'); } };
    const getRoleClass = (role) => { const roleKey = role.split(' ')[0].toLowerCase(); const validRoles = ['admin', 'screening', 'applicant']; return validRoles.includes(roleKey) ? `role-${roleKey}` : 'role-default'; };

    return (
        <>
            <div className="dashboard-container">
                <header className="dashboard-header">
                    <h1>Admin Dashboard</h1>
                    <div className="header-user-info">
                        <span>Hi, {user ? user.username : 'Admin'}</span>
                        <button className="logout-button" onClick={logout}>Logout</button>
                    </div>
                </header>
                <main className="dashboard-main">
                    <div className="admin-card"><h3>User Management</h3><p>View and manage user roles within the system.</p><div className="controls-container"><input type="text" placeholder="Search by user email..." className="search-input" value={searchQuery} onChange={(e) => setSearchQuery(e.target.value)} /><select className="filter-select custom-select" value={roleFilter} onChange={(e) => setRoleFilter(e.target.value)}><option value="All">All Roles</option>{roles.map(role => (<option key={role} value={role}>{role}</option>))}</select></div><table className="user-table"><thead><tr><th>Name</th><th>Email</th><th>Current Role</th><th>Action</th></tr></thead><tbody>{users.length > 0 ? (filteredUsers.map(u => (<tr key={u.id}><td>{u.name}</td><td>{u.email}</td><td><span className={`role-pill ${getRoleClass(u.role)}`}>{u.role}</span></td><td><button className="action-button" onClick={() => handleOpenAssignModal(u)}>Assign Role</button></td></tr>))) : (<tr><td colSpan="4" style={{ textAlign: 'center', padding: '2rem', color: '#6a737d' }}>Loading users or no users found...</td></tr>)}</tbody></table></div>
                    <div className="admin-card"><h3>Role Management</h3><p>Define new roles and their permissions.</p><button className="create-role-button" onClick={handleOpenAddRoleModal}>+ Add New Role</button></div>
                </main>
            </div>
            <Modal title="Assign Role" isOpen={isAssignModalOpen} onClose={() => setAssignModalOpen(false)}>{selectedUser && (<div><p>Assign a new role to <strong>{selectedUser.name}</strong>.</p><select className="custom-select" style={{ width: '100%', border: '1px solid #dcdfe6', borderRadius: '6px' }} defaultValue={selectedUser.role} onChange={(e) => handleAssignRole(e.target.value)}>{roles.map(role => <option key={role} value={role}>{role}</option>)}</select></div>)}</Modal>
            <Modal title="Add New Role" isOpen={isAddRoleModalOpen} onClose={() => setAddRoleModalOpen(false)}><div className="form-group"><label htmlFor="newRole">Role Name</label><input type="text" id="newRole" value={newRoleName} onChange={(e) => setNewRoleName(e.target.value)} placeholder="e.g., Document Verifier" style={{ width: '100%', padding: '0.8rem', border: '1px solid #dcdfe6', borderRadius: '6px' }} /></div><button className="form-button" onClick={handleAddNewRole}>Create Role</button></Modal>
        </>
    );
};
export default AdminDashboardPage;
