// src/pages/LoginPage.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './Form.css';

const LoginPage = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  // --- THIS IS THE MODIFIED FUNCTION ---
  const handleSubmit = (e) => {
    e.preventDefault();
    console.log('Login attempt:', { email, password });

    // --- UI-ONLY SIMULATION of a Backend ---
    // In a real app, you would send the email/password to a server.
    // The server would look up the user in a database and return their role.
    // Here, we simulate that logic directly.
    let userRole = '';
    if (email.toLowerCase().includes('admin')) {
      userRole = 'Admin';
    } else if (email.toLowerCase().includes('applicant')) {
      userRole = 'Applicant';
    } else {
      // Default to Screening Member for any other email
      userRole = 'Screening Member';
    }
    // --- End of Simulation ---

    console.log(`Simulated role for ${email}: ${userRole}`);

    // Navigate based on the "discovered" role
    switch (userRole) {
      case 'Admin':
        navigate('/admin-dashboard');
        break;
      case 'Screening Member':
        navigate('/dashboard');
        break;
      case 'Applicant':
        navigate('/applicant-dashboard');
        break;
      default:
        // This case is unlikely to be hit in our simulation
        navigate('/login');
    }
  };

  return (
      <div className="form-screen">
        <div className="form-card">
          <div className="form-header">
            <h2>Welcome Back</h2>
            <p>Sign in to the DRDO Portal</p>
          </div>
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input
                  type="email"
                  id="email"
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                  placeholder="e.g., admin@drdo.gov"
                  required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                  type="password"
                  id="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
              />
            </div>

            {/* --- The Role Dropdown has been REMOVED from here --- */}

            <button type="submit" className="form-button">Sign In</button>
          </form>
          <div className="form-footer">
            Don't have an account? <Link to="/signup">Sign Up</Link>
          </div>
        </div>
      </div>
  );
};

export default LoginPage;
